sourcesContent:
import { getConnection } from "./../database/database" ;

//Get
const getLanguages = async(req,res) => {
    try{
        const connection = await getConnection();
        const result = await connection.query("SELECT id, name, programmers FROM langueage");
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//Post
const addLanguage = async(req,res) => {
    try{
        const { name, programmers } = req.body;

        if(name===undefined || programmers===undefined){
            res.status(400).json({
                message: "Bad Request. Please fill all field"
            });
        }

        const language = { name, programmers }
        const connection = await getConnection();
        const result = await connection.query("INSERT INTO langueage SET ?", language);
        res.json({message: "Language Added"});
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//Get
const getLanguage = async(req,res) => {
    try{
        const {id} = req.params;
        const connection = await getConnection();
        const result = await connection.query("SELECT id, name, programmers FROM langueage WHERE id = ?", id);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//delete
const deleteLanguage = async(req,res) => {
    try{
        const {id} = req.params;
        const connection = await getConnection();
        const result = await connection.query("DELETE FROM langueage WHERE id = ?", id);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

//Update
const updateLanguage = async(req,res) => {
    try{
        const { id } = req.params;
        const { name, programmers } = req.body;

        if(name===undefined || programmers===undefined || id===undefined){
            res.status(400).json({
                message: "Bad Request. Please fill all field"
            });
        }

        const language = { name, programmers }
        const connection = await getConnection();
        const result = await connection.query("UPDATE langueage SET ? WHERE id = ?", [language,id]);
        res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }
};

export const methods = {
    getLanguages,
    addLanguage,
    getLanguage,
    deleteLanguage,
    updateLanguage
}

